    public function getSourceContext()
    {
        return new Source("{% extends ('base.html.twig') %}

{% block title %}Modification du profil utilisateur{% endblock %}

{% block body %}

<div class=\"container\">
    <h1 class=\"my-5\">Modification du profil</h1>

    {{ form_start(form) }}

    <div class=\"row-med\">
        <div class=\"col\">
            <div class=\"alert alert-light\">
                <h2 class=\"alert-heading\">Informations générales</h2>
                <hr>
                {{ form_row(form.firstName) }}
                {{ form_row(form.lastName) }}
                {{ form_row(form.email) }}
                {{ form_row(form.picture) }}
            </div>
        </div>
        <div class=\"col\">
            <div class=\"alert alert-light\">
                <h2 class=\"alert-heading\">Détails</h2>
                <hr>
                {{ form_row(form.introduction) }}
                {{ form_row(form.description) }}

                <hr>
                <button type=\"submit\" class=\"btn btn-success\">
                    <i class=\"fas fa-check\"></i>
                    Enregistrer les modifications
                </button>
                 <a href=\"{{ path('account_index') }}\" > <input type=\"button\" class=\"btn btn-primary float-right\" onclick=\"window.location.href='this.parentNode.href.value\" value=\"Retour à mon profil\" /></a>
            </div>
        </div>
    </div>

    {{ form_end(form) }}
</div>

{% endblock %}", "account/profile.html.twig", "/home/aubry/Bureau/projetbnb(copie)/projetbnbV9/templates/account/profile.html.twig");
